import axios from 'axios'
import { useState, useRef } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    useParams,
    useHistory
  } from "react-router-dom";

const EditUser = () =>
{
    const checkboxSub = useRef(null);
    const checkboxMov = useRef(null);
    let history = useHistory();


    function subFunc(checked) {
        if(checked == true)
        {
            checkboxSub.current.checked = true
        }
    }
    function movFunc(checked) {
        if(checked == true)
        {
            checkboxMov.current.checked = true
        }
    }

    const {name} = useParams()
    return(
    <div class="w3-container">
    <h1>Users</h1>
    <h2>Edit User: {name}</h2>
        <div class="w3-blue w3-padding-32 w3-bordered w3-code">
            First Name: <input type = "text" name= "fname"/><br/>
            Last Name: <input type = "text" name= "fname"/><br/>
            User Name: <input type = "text" name= "fname"/><br/>
            Session Time Out(Minutes): <input type = "text" name= "fname"/><br/>
            Created Date: <input type = "date" name= "fname"/><br/>
            Permissons : <br/>
             <input type = "checkbox" name = "ViewS" id = "ViewS" ref={checkboxSub} /> View Subscriptions <br/>
             <input type = "checkbox" name = "createS" id = "createS" onChange= {(e)=> subFunc(e.target.checked)}/> Create Subscriptions <br/> 
             <input type = "checkbox" name = "deleteS" id = "deleteS" onChange= {(e)=> subFunc(e.target.checked)} /> Delete Subscriptions <br/>
             <input type = "checkbox" name = "updateS" id = "updateS"  onChange= {(e)=> subFunc(e.target.checked)}/> Update Subscription <br/>
             <input type = "checkbox" name = "viewM" id = "viewM" ref={checkboxMov}/> View Movies <br/> 
             <input type = "checkbox" name = "createM" id = "createM"  onChange= {(e)=> movFunc(e.target.checked)}/> Create Movies <br/>
             <input type = "checkbox" name = "deleteM" id = "deleteM" onChange= {(e)=> movFunc(e.target.checked)}/> Delete Movies <br/>
             <input type = "checkbox" name = "updateM" id = "updateM" onChange= {(e)=> movFunc(e.target.checked)}/> Update Movie <br/><br/> 
             <input type = "submit" value = "Update"/>
             <input type = "button" value = "Cancel" onClick = {() => history.goBack()}/>

        </div>
    </div>
    )
}     
export default EditUser
