            setUsers(x.data)
            var p = x.data.permissions

        })

    let items = users.map((item,index) => {
        return <div key = {index} class="w3-green w3-hover-shadow w3-padding-32 w3-bordered w3-code">
            Name: {item.fullName}<br/>
            User Name: {item.userName}<br/>
            Session Time Out: {item.sessionTimeOut}<br/>
            Created Date: {item.created_date}<br/>
            Permissions: {item.permissions.join(",")}<br/><br/>
            <input  type = "button" value = "Edit" />
            <input type = "button" value = "Delete" />

        </div>
    })    
    return(
       <div class="w3-container">
       <h1 class = " font-family: Arial, Helvetica, sans-serif;">Users</h1>
       {items}
       </div>
    ) 
}

