import React, { Component } from 'react';
import axios from 'axios'

class App extends Component {

  constructor(props)
  {
    super(props)
    this.state = { users : []}
  }

  componentDidMount()
  {
    // axios.get('http://localhost:8000/api/persons').
    // then(resp => this.setState({users : resp.data}))
    axios.get('http://localhost:4000/routingToFront/UsersJson')
      .then(resp=> this.setState({users: resp.data}))
  }

  createuser = () =>
  {
    // let obj = {'fname' : 'Gil' , 'lname' : 'Ronen', 'city' :'Jerusalem'}
    // axios.post('http://localhost:8000/api/persons',obj)
    // .then(() => {})
  }
  render() {

    var list = this.state.users.map((item,index) =>
    {
      return <div key={index}>{item.userName} ,{item.fullName}</div>
    })

    return (
      <div className="App">
        {list} <br/><br/>
        <input type="button" value="Create User" onClick={this.createuser} />
      </div>
    );
  }
}

export default App;
